parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "emailBody": {
      "type": "string",
      "description": "The body content of the email reply to be sent to the prospect. (if escalate is true then this is unecessary)"
    },
      "reason": {
      "type": "string",
      "description": "Reasoning for your response."
    },
    "escalate": {
      "type": "boolean",
      "description": "True if you are unsure and need to escalate to your manager. False if you have enough information and are sure of your answer."
  },
    "kamexaInformation": {
      "type": "string",
      "description": "Summary of information you found from querying the kamexa_informatin database."
  }
  },
  "required": [
    "emailBody",
    "reason",
    "escalate"
  ]
}

